import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function GuestLogin() {
    const [currentUser, setCurrentUser] = useState("")
    let navigate = useNavigate();

    return (
        <div className="container">
            <div class="row justify-content-md-center mt-3 mb-3">
                <div class="col">
                    <div class="page-header">
                        <h1>
                            Welcome to <small class="text-muted">Doodle</small>
                        </h1>
                    </div>
                </div>
                <div class="col mt-3">
                    <h3 class="text-center">
                        Guest Portal
                    </h3>
                    <form class="form">
                        <div class="form-floating mb-3 control-group">
                            <input onChange={(event) => { setCurrentUser(event.target.value) }} name="inputGuestName" type="text" class="form-control" id="inputGuestName"
                                placeholder="Guest name" required />
                            <label for="inputGuestName" class="form-label">Guest Name</label>
                        </div>
                        <div class="position-relative">
                            <div class="position-absolute top-0 end-0">
                                <button onClick={() => {
                                    if (currentUser !== "") {
                                        localStorage.setItem("currentUser", currentUser)
                                        navigate('/manage-appointment')
                                        window.location.reload();
                                    }
                                }} class="btn btn-primary" type="button">Log In
                                </button>
                            </div>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );

}
export default GuestLogin
